import React, { Component } from "react";
import ReactTable from "react-table";
import "react-table/react-table.css";
import axios from "axios";
class App extends Component {
  constructor(props) {
    super(props);
    //Initial data from API
    this.state = {
      data: []
    };
    axios.get("http://localhost:3000/players").then(res => {
      // Update react-table
      this.setState({
        data: res.data.data,
        loading: false
      });
    });
  }
  render() {
    const columns = [
      {
        Header: "Player",
        accessor: "attributes.name",
        sortable: false,
        filterable: true
      },
      {
        Header: "Team",
        accessor: "attributes.team",
        sortable: false,
        filterable: false
      },
      {
        Header: "Pos",
        accessor: "attributes.position",
        sortable: false,
        filterable: false
      },
      {
        Header: "Att",
        accessor: "attributes.attempts",
        sortable: false,
        filterable: false
      },
      {
        Header: "Att/G",
        accessor: "attributes.attempts_per_game_average",
        sortable: false,
        filterable: false
      },
      {
        Header: "Yds",
        accessor: "attributes.total_yards",
        sortable: true,
        filterable: false
      },
      {
        Header: "Avg",
        accessor: "attributes.average_yards_per_attempt",
        sortable: false,
        filterable: false
      },
      {
        Header: "Yds/G",
        accessor: "attributes.yards_per_game",
        sortable: false,
        filterable: false
      },
      {
        Header: "TD",
        accessor: "attributes.total_touchdowns",
        sortable: true,
        filterable: false
      },
      {
        Header: "Lng",
        accessor: "attributes.longest_rush",
        sortable: true,
        filterable: false
      },
      {
        Header: "1st",
        accessor: "attributes.first_down",
        sortable: false,
        filterable: false
      },
      {
        Header: "1st%",
        accessor: "attributes.first_down_pct",
        sortable: false,
        filterable: false
      },
      {
        Header: "20+",
        accessor: "attributes.twenty_yards_each",
        sortable: false,
        filterable: false
      },
      {
        Header: "40+",
        accessor: "attributes.fourty_yards_each",
        sortable: false,
        filterable: false
      },
      {
        Header: "FUM",
        accessor: "attributes.fumbles",
        sortable: false,
        filterable: false
      }
    ];
    return (
      <ReactTable
        columns={columns}
        data={this.state.data}
        pages={this.state.pages}
        loading={this.state.loading}
        filterable
        onPageChange={pageIndex => {
          axios.get("http://localhost:3000/players").then(res => {
            // Update react-table
            this.setState({
              data: res.data.data,
              loading: false
            });
          });
        }}
        defaultPageSize={25}
        noDataText={"Loading..."}
        manual // informs React Table that you'll be handling sorting and pagination server-side
      />
    );
  }
}

export default App;
